$azul: #1F90FF;
$fucsia: #CC18AE;
$morado: #8466CC;
$verde_lima: #58C796;
$verde: #12E087;
$black: #282828;
$white: #E8E8E8;
$gris_oscuro: #474747;
$naranja: #F29B38;
$gris: #CDC3C7;
$rojo: #FF4242;

$txt-lg: 35px;
$txt-md: 25px;
$txt-sm: 15px;

* {
    margin: 0;
    padding: 0;
    font-family: 'Poppins', sans-serif;
    color: $black;
    font-weight: 300;
}

body {
    background-color: $white;
    
}

.neumorph-effect {
    box-shadow:  13px 13px 28px #A7A7A7, -13px -13px 28px #ffffff;
}

.hide {
    display: none !important;
    // background-color: red;
}

/* Nav section */
nav {
    background-color: $black;
    position: fixed;
    width: 90%;
    height: 10%;
    margin-left: 5%;
    bottom: 5%;
    border-radius: 15px;
    display: flex;
    justify-content: space-around;
    align-content: center;
    align-items: center;

    .icon-non-active {
        stroke: $white;
    }

    .icon-active {
        stroke: $verde;
    }
}

/* Main Section */
.main-section {
    width: 100%;
    padding: 10% 5%;
    box-sizing: border-box;
    height: 100%;


    .presentation-banner {
        width: 100%;
        height: 15%;
        display: grid;
        grid-template-columns: 3fr 1fr;

        .say-hello-title {
            font-size: $txt-lg;
            margin-top: 5px;
            font-weight: 800;
        }

        .welcome-subtitle {
            color: $gris_oscuro;
            font-weight: 500;
            font-size: $txt-md;
            font-stretch: condensed;
        }

        img {
            width: 100%;
            height: 110%;
            border-radius: 10px;
            border-top-right-radius: 35px;
            border-bottom-left-radius: 35px;
            object-fit: cover;
        }
    }

    .main-graphs-widget {
        margin-top: 20%;
        border-radius: 5%;
        padding: 5%;
        display: flex;
        flex-direction: column;

        .main-widgets-titles{
            ul{
                display: flex;
                list-style: none;
                justify-content: space-around;

                li{
                    display: flex;
                    flex-direction: column;
                    place-items: center;

                    span{
                        color: $gris_oscuro;
                        font-weight: 400;
                    }
                    .goal-title{
                        color: $verde;
                    }
                    .average-title{
                        color: $naranja;
                    }
                    .necesary-title{
                        color: $morado;
                    }
                    
                    :nth-child(1){
                        font-size: $txt-lg;
                        font-weight: 700;
                        margin-bottom: -10px;
                    }
                    margin-bottom: 30px;
                }
            }
        }
        .main-widgets-graph{
            background-color: $gris;
            height: 150px;
            border-radius: 15px;
            display: flex;
            place-content: center;
            place-items: center;
        }
        .main-widget-btns{
            margin-top: 20px;
            display: flex;
            justify-content: space-around;

            .main-widget-btn{
                background-color: $white;
                border: none;
                padding: 5% 10%;
                border-radius: 15px;
                font-weight: 500;
                cursor: pointer;
            }
            .main-widget-btn:nth-child(1){
                color: $naranja;
            }
        }
        
    }

    .main-calendar-widget {
        margin-top: 10%;
        border-radius: 20px;
        padding: 5%;
        margin-bottom: 10%;

        .calendar-widget-header{
            display: flex;

            .calendar-btn{
                background-color: $white;
                border: none;
                padding: 1% 2%;
                border-radius: 15px;
                font-weight: 500;
                stroke: $azul;
                cursor: pointer;
                margin-right: 5%;
            }
        }
        .calendar-widget-text{
            
            display: flex;
            flex-direction: column;

            span:nth-child(1){
                font-weight: 600;
                margin-bottom: -5px;
                margin-top: 5px;
            }
        }
        .calendar-widget-list{
            display: flex;
            margin-top: 40px;
            justify-content: space-between;

            div{
                ul{
                    list-style: none;
                    display: flex;
                    height: 100%;
                    flex-direction: column;
                    justify-content: space-around;

                    
                    li{
                        margin: 10px 0px;
                    }
                }
            }

            .calendar-widget-subjects, .calendar-widget-subjects-notes{
                ul{
                    li{
                        display: flex;
                        flex-direction: column;

                        span:nth-child(1){
                            font-weight: 700;
                            font-size: 17px;
                        }
                        span:nth-child(2){
                            font-size: $txt-sm;
                            margin-top: -5px;
                        }
                        .calendar-widget-note{
                            font-size: $txt-md !important;
                            color: $morado;
                        }
                    }
                }
            }
            
        }
    }
}
